package com.example.fptacademysystem.controller;import com.example.fptacademysystem.model.StaffAccount;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpSession;@Controllerpublic class AdminAttendanceController {    @RequestMapping(value = "/admin/dashboard/attendance/report", method = RequestMethod.GET)    public String reportAttendance(Model model, HttpSession session) {        if (session.getAttribute("staffroll") != null){            return "admin/attendance/report-attendance";        } else {            return "login/admin-login";        }    }    @RequestMapping(value = "/admin/dashboard/attendance", method = RequestMethod.GET)    public String index(Model model, HttpSession session) {        if (session.getAttribute("staffroll") != null) {            return "admin/attendance/main-attendance";        } else {            return "login/admin-login";        }    }}