/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. */package com.example.fptacademysystem.repository;import java.util.List;import javax.transaction.Transactional;import com.example.fptacademysystem.model.BranchCampus;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Modifying;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;/** * * @author kille */public interface BranchCampusRepository extends JpaRepository<BranchCampus,Integer> {    @Query("SELECT b FROM BranchCampus b WHERE b.removeat='No'")    List<BranchCampus> findall();    @Query("SELECT b FROM BranchCampus b WHERE b.removeat='No' AND b.branchcamid = :branchcamid")    BranchCampus findBranchById(@Param("branchcamid")int branchcamid);    @Modifying(clearAutomatically = true)    @Transactional    @Query("UPDATE BranchCampus b SET b.removeat='Yes' WHERE b.branchcamid= :branchcamid")    void deleteid(@Param("branchcamid") int branchcamid);}