package com.example.fptacademysystem.repository;import com.example.fptacademysystem.model.Attendance;import com.example.fptacademysystem.model.Student;import com.example.fptacademysystem.model.Timetable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface StudentAttendanceRepository extends JpaRepository<Attendance, Integer> {    @Query("SELECT a from Attendance a where a.stuid= :stuid AND a.timetableid= :timetableid and a.removeat='No'")    Attendance findAttendanceByStuidAndTimetableid(@Param("stuid")Student stuid,@Param("timetableid") Timetable timetableid);}