package com.example.fptacademysystem.repository;import java.util.List;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import com.example.fptacademysystem.model.SubjectSubjectdetails;@Repositorypublic interface SubjectSubjectDetailsRepository extends JpaRepository<SubjectSubjectdetails, Integer> {    public static final String FIND_SSD = "select ss from SubjectSubjectdetails ss WHERE ss.courid=:coursesID AND ss.semid=:semID";    @Query(value = FIND_SSD)    List<SubjectSubjectdetails> findSSD(@Param("coursesID") int coursesID,@Param("semID") int semID);}