package com.example.fptacademysystem.services;import com.example.fptacademysystem.model.Courses;import com.example.fptacademysystem.repository.CourseRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CoursesService implements ICourses {    @Autowired    CourseRepository courseRepository;    @Override    public List<Courses> Findall() {        return courseRepository.findall();    }    @Override    public void create(Courses courses) {        courseRepository.save(courses);    }    @Override    public Courses FindOne(int Id) {        return courseRepository.findById(Id).get();    }    @Override    public void update(Courses courses) {        courseRepository.save(courses);    }    @Override    public void delete(int Id) {        courseRepository.deleteid(Id);    }}