package com.example.fptacademysystem.services;import com.example.fptacademysystem.helper.ExcelHelper;import com.example.fptacademysystem.model.Student;import com.example.fptacademysystem.repository.StudentRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.web.multipart.MultipartFile;import java.io.ByteArrayInputStream;import java.io.IOException;import java.util.List;@Servicepublic class ExcelService {    @Autowired    StudentRepository repository;    public void save(MultipartFile files) {        try {            List<Student> student = ExcelHelper.excelToStud(files.getInputStream());            repository.saveAll(student);        } catch (IOException e) {            throw new RuntimeException("fail to store excel data: " + e.getMessage());        }    }    public ByteArrayInputStream load() {        List<Student> students = repository.findAll();        ByteArrayInputStream in = ExcelHelper.tutorialsToExcel(students);        return in;    }}