package com.example.fptacademysystem.services;import com.example.fptacademysystem.helper.ExcelHelper;import com.example.fptacademysystem.helper.ExcelSubjectHelper;import com.example.fptacademysystem.model.Student;import com.example.fptacademysystem.model.Subject;import com.example.fptacademysystem.repository.SubjectRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.web.multipart.MultipartFile;import java.io.ByteArrayInputStream;import java.io.IOException;import java.util.List;@Servicepublic class ExcelSubjectService {    @Autowired    SubjectRepository repository;    public void save(MultipartFile files) {        try {            List<Subject> subject = ExcelSubjectHelper.excelToSubject(files.getInputStream());            repository.saveAll(subject);        } catch (IOException e) {            throw new RuntimeException("fail to store excel data: " + e.getMessage());        }    }    public ByteArrayInputStream load() {        List<Subject> subjects = repository.findAll();        ByteArrayInputStream in = ExcelSubjectHelper.tutorialsToExcel(subjects);        return in;    }}