package com.example.fptacademysystem.services;import com.example.fptacademysystem.model.Role;import com.example.fptacademysystem.repository.RoleRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class RoleService implements IRole {    @Autowired    RoleRepository roleRepo;    @Override    public List<Role> findAll() {        return roleRepo.findAllRole();    }    @Override    public Role findRole(int roleid) {        return roleRepo.findRole(roleid);    }    @Override    public void deleteRole(int roleid) {        roleRepo.deleteRole(roleid);    }    @Override    public void createRole(Role role) {        roleRepo.save(role);    }    @Override    public void updateRole(Role role) {        roleRepo.save(role);    }}